import java.util.Scanner;
public class forloop {

	public static void main(String[] args) {
		// TODO Auto-generated method 
      for(int i=5;i>=1;i--);
      {
    	  int i=0; 
    	  for(int j=1;j>=i;j++);
    	  {
    System.out.println("*");
    	  }
    	  System.out.println();
      }
    
	}

}
